
public class Chap3If {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String name = "Rin";
		
		name = "jane";
		
		if (name == "Rin" ) {
			System.out.println("저의 이름입니다.");
		} else if (name == "jane") {
			System.out.println("친구이름입니다");
		} else {
			System.out.println("넌 누구냐??");
		}
	}

}
